le="border-basic" icon_border_thickness="2px" icon_color="Accent-Color" icon_padding="20px" icon_linea="icon-ecommerce-receipt-dollar" icon_size="60"][vc_column_text]\r\n<h4 style="text-align: center;">Savings</h4>\r\n<p style="text-align: center;">Reduce encryption management cost and complexity. Leverage the scalability and elasticity of cloud services and support multi-tenancy without jeopardizing security.</p>\r\n[/vc_column_text][/vc_column_inner][/vc_row_inner][/vc_column][/vc_row][vc_row type="full_width_background" full_screen_row_position="middle" scene_position="center" text_color="dark" text_align="left" top_padding="90" overlay_strength="0.3"][vc_column column_padding="no-extra-padding" column_padding_position="all" background_color_opacity="1" background_hover_color_opacity="1" width="1/1" tablet_text_alignment="default" phone_text_alignment="default"][vc_column_text]\r\n<h2 style="text-align: center;">Our Partners</h2>\r\n<h4 style="text-align: center;">Because Collaboration Is Key</h4>\r\n[/vc_column_text][carousel script="owl_carousel" desktop_cols="4" desktop_small_cols="3" tablet_cols="2" mobile_cols="1" column_padding="0" autorotate="true"][item title="Item" id="1474576285768-0-0" tab_id="1474576285776-3"][image_with_animation image_url="6266" alignment="center" animation="Fade In" box_shadow="none" max_width="100%"][vc_column_text][/vc_column_text][/item][item title="Item" id="1474576368036-0-8" tab_id="1474576285776-3"][image_with_animation image_url="6269" alignment="center" animation="Fade In" box_shadow="none" max_width="100%"][vc_column_text][/vc_column_text][/item][item title="Item" id="1483381111094-0-3" tab_id="1474576285776-3"][image_with_animation image_url="6270" alignment="center" animation="Fade In" box_shadow="none" max_width="100%"][vc_column_text][/vc_column_text][/item][item title="Item" id="1483381111997-0-6" tab_id="1474576285776-3"][image_with_animation image_url="6267" alignment="center" animation="Fade In" box_shadow="none" 